import { Output, EventEmitter, Directive } from '@angular/core';
import { CollectionFilter, ComparisonOperator, } from 'ngx-kit-business/types';

@Directive()
export class FiltersContainerComponent {
  filters: CollectionFilter[] = [];
  searchFilter!: CollectionFilter;

  @Output() filtersChange: EventEmitter<CollectionFilter[]> = new EventEmitter();
  @Output() searchFilterChange: EventEmitter<CollectionFilter> = new EventEmitter();

  setFilterValue(
    id: string,
    value: any,
    active?: boolean,
    operator?: ComparisonOperator
  ) {
    const me = this;
    let filter = me.getFilter(id);
    if (filter) {
      filter.value = value;
      filter.active = true;
      filter.operator = operator ? operator : filter.operator;
      // Posibilidades de desactivar el filtro
      if (
        filter.type == 'string' &&
        (!value || value == filter.inactiveOnValue)
      ) {
        filter.active = false;
      }
      if (filter.type == 'numeric' && value == filter.inactiveOnValue) {
        filter.active = false;
      }
      if (filter.type == 'list' && (!value || !value.length)) {
        filter.active = false;
      }
      if (filter.type == 'date' && (!value || !value.length)) {
        filter.active = false;
      }
      // El valor active predomina si es establecido esplicitamente
      if (active === true || active === false) {
        filter.active = active;
      }
    }
    me.fireEvent();
  }
  setSearchFilterValue(value: string) {
    this.searchFilter = { ...this.searchFilter, value };
    this.searchFilterChange.emit(this.searchFilter);
  }
  activeFilter(field: string, active: boolean) {
    const me = this;
    me.filters.forEach((item) => {
      if (item.field == field) {
        item.active = !!active;
      }
    });
  }
  addOrRemoveListFilterItemValue(id: string, itemValue: any, add: boolean) {
    const me = this;
    let newValue = [];
    let filter = me.getFilter(id);
    if (filter) {
      newValue = filter.value || [];
      // Add value
      if (add) {
        // Valor no está en la lista
        if (newValue.indexOf(itemValue) == -1) {
          newValue.push(itemValue);
        }
      }
      // Remove value
      else {
        newValue = newValue.filter((value: any) => value != itemValue);
      }
      filter.value = newValue;
    }
    me.fireEvent();
  }

  fireEvent() {
    this.filtersChange.emit(this.getActiveFilters());
  }

  /**
   * Retorna solo los filtros activos
   */
  getActiveFilters() {
    let out = this.filters.filter((filter) => {
      if (filter.type == 'list') {
        return filter.value && filter.value.length;
      }
      return filter.active;
    });
    return out;
  }
  getFilter(id: string): CollectionFilter | null {
    const me = this;
    let out = null;
    me.filters.forEach((item) => {
      if (item.id == id) {
        out = item;
      }
    });
    return out;
  }
}

import {
  UntypedFormGroup,
  AbstractControl,
  Validators,
  UntypedFormBuilder,
} from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { OnInit, Directive, Injector, HostBinding } from '@angular/core';
import { Observable } from 'rxjs';
import { DialogService } from './services/dialog.service';
import { NotificationsService } from './services/notifications.service';
import { EMAIL_PATTERN, FormBuilderHelper, FormField, FormFieldOptions, URL_PATTERN } from './form-builder-helper';

/**
 * Form Component
 * EDITAR Y GUARDAR
 * Clase base para todos los formularios
 */
@Directive()
export class FormDialogComponent<T> implements OnInit {
  @HostBinding('class')
  hostCls: string = 'ngbiz-custom-material__mat-dialog--form';
  mode!: 'add' | 'edit';
  //
  data: any = {};
  parent: any = {};
  model: T;
  modelIdProperty = 'id';

  apiUrl: string = '';
  dataPath: string = '';
  // Texts
  fieldRequiredErrorMessageText = 'Requerido';
  msgSuccessfulCreateText = 'Creado correctamente';
  msgSuccessfulUpdateText = 'Actualizado correctamente';
  invalidValuesMessage =
    'Complete los campos requeridos y corrija los errores identificados';
  /**
   * FormGrup asignado al formulario
   */
  formModel!: UntypedFormGroup;
  protected formFields: { [key: string]: FormFieldOptions } = {};
  /**
   * Si es true se usará el metodo getRawValue() obtener los valores del formulari
   * caso contrario solo se usará la propiedad value.
   */
  useFormRawValue!: boolean;
  /**
   * Llamar al metodo buildFormModel al inicio.
   */
  buildFormModelOnInit: boolean = true;

  /**
   * @cfg
   * Definir los campos que tendrá el formulario
   */
  protected fields: FormField[] = [];

  /**
   * Valores iniciales del fomulario
   */
  protected values: any;
  // Services
  protected api;
  protected notifications: NotificationsService;
  protected formBuilder: UntypedFormBuilder;
  protected dialogService: DialogService;
  protected fbHelper: FormBuilderHelper;
  protected dialogRef: MatDialogRef<any>;
  _isReady!: boolean;
  _isSaving!: boolean;
  constructor(
    protected injector: Injector,
    api: any,
    dialogRef: any,
    inputData: any
  ) {
    this.notifications = injector.get(NotificationsService);
    this.formBuilder = injector.get(UntypedFormBuilder);
    this.dialogService = injector.get(DialogService);
    this.fbHelper = injector.get(FormBuilderHelper);
    this.api = api;
    this.dialogRef = dialogRef;
    this.model = inputData?.model ? inputData.model : this.createModel();
    this.parent = inputData ? inputData.parent : {};
    this.data = inputData ? inputData.data : {};
  }

  createModel() {
    return {};
  }

  /**
   * Enviar datos
   */
  onSubmit() {
    const me = this;
    // Si esta guardando.
    if (me._isSaving) {
      return;
    }
    // Si los datos son incorrectos
    if (!me.isValid()) {
      me.showInvalidValuesMessage();
      return;
    }
    me.setStatusSaving();
    let postModel = me.getPostData();
    me.getSaveService(postModel).subscribe(
      (rec) => {
        me.notifications.snackSuccess(
          me.isCreationMode()
            ? me.msgSuccessfulCreateText
            : me.msgSuccessfulUpdateText
        );
        me.dialogRef.close(rec);
      },
      (xhr) => {
        if (xhr.status == 401) {
          me.dialogRef.close();
          return;
        }
        me.handleServerError(xhr);
        me.setStatusReady();
      }
    );
  }

  isNew() {
    return this.mode == 'add';
  }

  getSaveService(model: any): Observable<any> {
    return this.api.save(model, model[this.modelIdProperty]);
  }

  showInvalidValuesMessage() {
    const me = this;
    me.notifications.snackWarning(me.invalidValuesMessage);
  }

  /**
   * Este metodo puede ser sobre escrito para manejar internamente las excepciones del servidor
   */
  handleServerError(xhr: any) {
    this.notifications.handleXhrError(xhr, this);
  }

  isCreationMode() {
    return this.mode == 'add';
  }

  isEditionMode() {
    return this.mode == 'edit';
  }

  ngOnInit(): void {
    const me = this;
    me.mode = me.model && (me.model as any)[me.modelIdProperty] ? 'edit' : 'add';
    if (me.buildFormModelOnInit) {
      me.defineFormModel();
    }
  }

  isValid() {
    return this.formModel.valid;
  }

  /**
   * Este metodo retorna los campos que son enviados al servidor
   */
  getPostData() {
    const me = this;
    if (this.useFormRawValue) {
      return { ...me.model, ...me.formModel.getRawValue() };
    }
    return { ...me.model, ...me.formModel.value };
  }
  /**
   * Permite determinar si el campo debe incluirse en el formulario
   * @param field
   * @returns
   */
  hasField(field: string): boolean {
    return this.formFields && this.formFields.hasOwnProperty(field);
  }
  /**
   * Retorna el campo del formulario solicitado
   * @param name
   */
  getFormControl(name: string): AbstractControl | null {
    return this.formModel.get(name);
  }

  getFieldProperty(fieldName: string, prop: keyof FormFieldOptions) {
    return this.formFields[fieldName] ? this.formFields[fieldName][prop] : null;
  }

  hasError(fieldName: string, errorType: string) {
    return this.getFormControl(fieldName)?.hasError(errorType);
  }

  isFieldRequired(fieldName: string) {
    return this.hasError(fieldName, 'required');
  }

  hasMinLengthError(fieldName: string) {
    return this.hasError(fieldName, 'minlength');
  }

  hasMaxLengthError(fieldName: string) {
    return this.hasError(fieldName, 'maxlength');
  }

  hasPatternError(fieldName: string) {
    return this.hasError(fieldName, 'pattern');
  }

  getRequiredErrorMessage(_fieldName: string = '') {
    return this.fieldRequiredErrorMessageText;
  }

  getMinLengthErrorMessage(fieldName: string) {
    return `Mín ${this.getFieldProperty(fieldName, 'minLength')} caracteres.`;
  }

  getMaxLengthErrorMessage(fieldName: string) {
    return `Máx ${this.getFieldProperty(fieldName, 'maxLength')} caracteres.`;
  }

  shouldShowErrorMessage(fieldName: string) {
    const me = this;
    let control = me.getFormControl(fieldName);
    return control && control.invalid && (control.dirty || control.touched);
  }

  getFieldValue(field: string) {
    return this.getFormControl(field)?.value;
  }

  setFieldValue(field: string, value: any) {
    this.getFormControl(field)?.setValue(value);
  }

  isFieldValid(field: string) {
    return this.getFormControl(field)?.valid;
  }

  private buildFormModel() {
    const me = this;
    let formGroupObjet: any = {},
      fieldValue,
      fieldPropsObj;
    me.fields.forEach((field) => {
      field = me.preProcessDefinedField(field);
      fieldValue = (me.model as any)[field.name];
      fieldValue = fieldValue !== undefined ? fieldValue : field.defaultValue;
      // disabled Field
      if (field.disabled) {
        fieldPropsObj = { value: fieldValue, disabled: true };
      } else {
        fieldPropsObj = fieldValue;
      }
      formGroupObjet[field.name] = [fieldPropsObj, me.getValidators(field)];
    });
    if (me.formBuilder) me.formModel = me.formBuilder.group(formGroupObjet);
  }

  defineFormModel() {
    const me = this;
    const fields: any = {};
    for (const fieldName of Object.keys(me.formFields)) {
      fields[fieldName] = me.transformDefinedField(fieldName, me.formFields[fieldName]);
    }
    me.formModel = me.fbHelper.buildFormModel(
      fields,
      me.getValuesForFormModel(me.model),
    );
  }

  /**
 * Permite modificar la definición original del campo antes de construir el formControl
 * @param field
 * @returns
 */
  protected transformDefinedField(_fieldName: string, field: FormFieldOptions) {
    return field;
  }

  /**
   * Permite modificar los valores que se usarán para construir el formControl
   * @param field
   * @returns
   */
  protected getValuesForFormModel(values: T): any {
    return values;
  }

  protected updateForm() {
    this.buildFormModel();
  }

  /**
   * Permite modificar la definición original del campo antes de construir un formControl
   * @param field
   * @returns
   */
  protected preProcessDefinedField(field: FormField) {
    return field;
  }

  /**
   * Retronar un arreglo de validadores
   * @param field
   */
  private getValidators(field: FormField) {
    let out = [];
    if (field.required) {
      out.push(Validators.required);
    }
    if (field.min) {
      out.push(Validators.min(field.min));
    }
    if (field.max) {
      out.push(Validators.max(field.max));
    }
    if (field.minLength) {
      out.push(Validators.minLength(field.minLength));
    }
    if (field.maxLength) {
      out.push(Validators.maxLength(field.maxLength));
    }
    if (field.email) {
      out.push(Validators.email);
    }
    if (field.emailPattern) {
      out.push(Validators.pattern(EMAIL_PATTERN));
    }
    if (field.urlPattern) {
      out.push(Validators.pattern(URL_PATTERN));
    }
    if (field.pattern) {
      out.push(Validators.pattern(field.pattern));
    }
    if (field.customValidation) {
      out.push(field.customValidation);
    }
    return out;
  }

  setFieldDisabled(fieldName: string, disabled: boolean) {
    const me = this;
    me.fields = me.fields.map((field) => {
      if (field.name == fieldName) {
        field.disabled = disabled;
      }
      return field;
    });
    me.updateForm();
  }

  // Estado por defecto
  setStatusReady() {
    this._isReady = true;
    this._isSaving = false;
  }

  // Guardando...
  setStatusSaving() {
    this._isSaving = true;
    this._isReady = false;
  }

  // Cierra inmediatamente la interfaz
  protected __forceClose() {
    this.dialogRef.close();
  }

  getApiUrl() {
    return this.apiUrl;
  }

  getDataPath() {
    return this.dataPath;
  }

  getCollectionUrl() {
    return this.getApiUrl() + '/' + this.getDataPath();
  }

}

import {
  OnInit,
  Injector,
  ViewChild,
  AfterViewInit,
  Directive,
  HostListener,
  Output,
  EventEmitter,
} from '@angular/core';
import { NotificationsService } from './services/notifications.service';
import { HttpClient } from '@angular/common/http';
import { MatDialog } from '@angular/material/dialog';
import { MatTable } from '@angular/material/table';
import { DialogService } from './services/dialog.service';
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { SelectionModel } from '@angular/cdk/collections';
import { ActivatedRoute, Router } from '@angular/router';

import { HttpCollectionService, CollectionQueryParams, QueryFilter, CollectionResponse } from 'ngx-kit-business/http';
import { DataErrorViewComponent } from './data-error-view/data-error-view.component';

import { of, Subscription, Subject } from 'rxjs';
import { switchMap, catchError } from 'rxjs/operators';
import { StringUtils } from './utils/string-utils';


enum COMPONENT_TYPE {
  PAGE = 'page',
  COMPONENT = 'component',
}
@Directive()
export class TableViewComponent<T> implements OnInit, AfterViewInit {
  @Output() selectionChange = new EventEmitter<any[]>();
  /**
   * Tipo de componente
   *  - page: Si el componente que extiende está asociado con una ruta.
   *  - component: Si el componente extendido esta dentro de otro componente
   *
   */
  protected type: string = 'page';
  // @config llama a loadData inmediatamente
  autoload = true;
  apiUrl: string = '';
  sendModelOnDelete = false;
  requestParams: { [key: string]: string | number | boolean } = {};
  dataPath: string = '';
  displayedColumns: string[] = [];
  itemSingularName: string = 'ítem';
  itemPluralName: string = 'ítems';
  itemSingularArticle: string = 'el';
  // Si se asigna un valor a la propiedad en una subclase, ya no se usará confirmDeleteTitleTpl
  confirmDeleteTitle: string = '';
  confirmDeleteTitleTpl = 'Eliminar {itemName}';
  deleteItemQuestionText = '';
  deleteItemQuestionTextTpl = '¿Seguro que desea eliminar {itemArticle} {itemName}?';
  deleteItemConfirmDialogContentTpl = '{question}<div class="mt-2"><strong style="text-transform: capitalize;">{itemName}</strong></div>';
  deleteItemConfirmButtonText = 'ELIMINAR';
  successfullyDeletedItemNotificationTpl = '{itemName} eliminado correctamente';
  protected editorClass: any;
  protected editionDisabled = false;

  // Propiedades disponibles
  pageSize = 25;
  pageIndex = 0;
  // Guarda una copia del array de datos recibidos del servicio,
  // Nota: solo se clonan los objetos de primer nivel usando
  // map(e => { return { ...e } });
  _rawData!: T[];
  // Guarda una copia de la data que ha pasado por el metodo transformServiceResponseData
  _rawTransformedData!: T[];
  // Contiene la data que es visualizada en la interfaz
  data!: T[];
  selectedRow: any;
  totalRecords = 0;
  selectedRowIndex: number = -1;
  selection: SelectionModel<any>;
  selectionMultiple: boolean = false;
  modelIdProperty = 'id';
  modelNameProperty = 'name';
  // Services
  protected notifications: NotificationsService;
  protected http: HttpClient;
  protected matDialog: MatDialog;
  protected dialogService: DialogService;
  protected httpGetList: HttpCollectionService;
  protected router: Router;
  protected route: ActivatedRoute;

  // Child components
  @ViewChild(MatTable) table?: MatTable<any>;
  @ViewChild(MatSort) sort?: MatSort;
  @ViewChild(MatPaginator) paginator?: MatPaginator;
  @ViewChild(DataErrorViewComponent) errorView?: DataErrorViewComponent;
  filters: QueryFilter[] = [];
  $fields: string = '';
  localSearchFilter!: QueryFilter;
  searchFilter!: QueryFilter;
  // Propiedades de estado
  _isLoadingData = false;
  _didLoadDataFailed = false;
  _loadSubcription?: Subscription;

  //
  clearDataOnFail = true;
  private loadObs = new Subject();
  public load = this.loadObs.asObservable();
  private errorStatus: number = 0;
  constructor(protected injector: Injector) {
    this.notifications = injector.get(NotificationsService);
    this.http = injector.get(HttpClient);
    this.httpGetList = injector.get(HttpCollectionService);
    this.matDialog = injector.get(MatDialog);
    this.router = injector.get(Router);
    this.route = injector.get(ActivatedRoute);
    this.dialogService = injector.get(DialogService);
    // Selection model
    this.selection = new SelectionModel<any>(this.selectionMultiple, []);
  }
  // Keyboard listeners
  @HostListener('window:keyup', ['$event'])
  keyEvent(e: KeyboardEvent) {
    const me = this;
    if ((e.target as Element).tagName != 'BODY') {
      return;
    }
    // Reload data
    if (e.code === 'KeyA') {
      me.loadData();
      return;
    }
    // Open editor
    if (e.code === 'Enter' && me.getSelection().length == 1 && me.editorClass) {
      me.editItemHandler(me.selection.selected[0]);
      return;
    }
    // Select Next
    if (e.code === 'ArrowDown' && me.getSelection().length == 1) {
      me.selectNext();
      return;
    }
    // Select Previus
    if (e.code === 'ArrowUp' && me.getSelection().length == 1) {
      me.selectPrevious();
    }
  }

  loadData() {
    this.loadObs.next(null);
  }

  _subscribeLoadEvent() {
    const me = this;
    me._loadSubcription = me.load
      .pipe(
        switchMap(() => {
          me.beforeLoad();
          me._isLoadingData = true;
          me._didLoadDataFailed = false;
          return me.httpGetList
            .get<CollectionResponse<T>>(me.getRequestUrl(), me.getBaseRequestParams())
            .pipe(
              catchError((xhr) => {
                me._isLoadingData = false;
                me._didLoadDataFailed = true;
                me.handleServerError(xhr);
                return of(null);
              })
            );
        })
      )
      .subscribe(me.onDataServiceResponse.bind(me));
  }
  onDataServiceResponse(res: any) {
    const me = this;
    // When error has happened and was catched by catchError
    if (me._didLoadDataFailed) return;

    let data: T[] = [],
      total = 0;
    // res: {data: any[], total: number }
    if (
      Object.prototype.toString.call(res) == '[object Object]' &&
      Array.isArray(res.data)
    ) {
      data = res.data;
      if (isNaN(res.total)) {
        total = data.length;
      } else {
        total = +res.total > data.length ? +res.total : data.length;
      }
    }
    // res: any[]
    else if (Array.isArray(res)) {
      data = res;
      total = res.length;
    }
    me._rawData = data.map(e => { return { ...e } });
    me._rawTransformedData = me.transformServiceResponseData(data);
    me.data = me._rawTransformedData.map(e => { return { ...e } });

    me.totalRecords = total;
    me._isLoadingData = false;
  }
  transformServiceResponseData(data: any[]) {
    return data;
  }

  /**
   * Hook
   * Es llamado antes de solicitar datos al servidor
   */
  beforeLoad() { }

  getSelection() {
    const me = this;
    let selection = me.selection.selected;
    if (selection.length) {
      // Single selection
      if (!me.selectionMultiple) {
        if (me.recordExists(selection[0])) {
          return selection;
        }
        me.selection.clear();
        return [];
      } else {
        selection = selection.filter((item) => me.recordExists(item));
        me.selection.clear();
        selection.forEach((item) => me.selection.select(item));
      }
    }
    return me.selection.selected;
  }

  recordExists(rec: any) {
    let out = false;
    let data = this.data || [];
    data.forEach((item: any) => {
      if (item == rec) {
        out = true;
      }
    });
    return out;
  }

  getRecordByIndex(idx: number) {
    let data = this.data || [];
    return data[idx] ? data[idx] : null;
  }

  getRecordIndex(rec: any) {
    let out = -1;
    let data = this.data || [];
    data.forEach((item: any, idx: number) => {
      if (item === rec) {
        out = idx;
      }
    });
    return out;
  }

  selectNext() {
    const me = this;
    // Matener condicionales separadas
    if (!me.selection.hasValue()) {
      return;
    }
    if (me.selectionMultiple) {
      return;
    }
    let index = me.getRecordIndex(me.selection.selected[0]);
    if (me.validIndex(index)) {
      let nextRec = me.getRecordByIndex(index + 1);
      if (nextRec) {
        me.selection.select(nextRec);
      }
    }
  }

  selectPrevious() {
    const me = this;
    // Matener condicionales separadas
    if (!me.selection.hasValue()) {
      return;
    }
    if (me.selectionMultiple) {
      return;
    }
    let index = me.getRecordIndex(me.selection.selected[0]);
    if (me.validIndex(index)) {
      let nextRec = me.getRecordByIndex(index - 1);
      if (nextRec) {
        me.selection.select(nextRec);
      }
    }
  }

  validIndex(index: number) {
    return index != -1;
  }

  getBaseRequestParams(): CollectionQueryParams {
    const me = this,
      paginator = me.paginator,
      sorter = me.sort,
      filters = me.getFilters(),
      fields = me.$fields;
    let out: CollectionQueryParams = {};
    // Paginación
    if (paginator) {
      out.page = paginator.pageIndex + 1;
      out.limit = paginator.pageSize;
    } else {
      out.page = me.pageIndex + 1;
      out.limit = me.pageSize;
    }
    // Query
    if (me.searchFilter?.value) {
      out.query = me.searchFilter?.value;
    }
    // Filters
    if (filters && filters.length) {
      out.filters = filters;
    }
    // Sorters
    // Por ahora solo está soportando un simple sort
    if (sorter && sorter.active && sorter.direction) {
      out.sorters = [{ field: sorter.active, direction: sorter.direction }];
    }
    if (fields) {
      out.fields = fields;
    }
    return out;
  }


  getApiUrl() {
    return this.apiUrl;
  }

  getDataPath() {
    return this.dataPath;
  }

  getCollectionUrl() {
    return this.getApiUrl() + '/' + this.getDataPath();
  }
  getDeleteResourceUrl(model: T) {
    const me = this;
    let modelId = (model as any)[me.modelIdProperty];
    return me.getCollectionUrl() + '/' + modelId
  }
  getRequestUrl(): string {
    const me = this,
      queryParams = me.getRequestParams(),
      url = me.router
        .createUrlTree([me.getDataPath()], { queryParams })
        .toString();
    return me.getApiUrl() + url;
  }

  getRequestParams(): { [key: string]: number | string | boolean } {
    return this.requestParams;
  }

  /**
   * ACTION HANDLERS
   */
  addItemHandler() {
    const me = this;
    const dialogRef = me.matDialog.open(me.editorClass, {
      data: me.getEditorData(),
      position: { top: '50px' },
      disableClose: true,
    });
    // Cuando el formulario se cierra.
    dialogRef.afterClosed().subscribe((data) => {
      me.afterCreateEditorClosed(data);
    });
  }

  editItemHandler(rec: any) {
    const me = this;
    if (me.editionDisabled) {
      return;
    }
    const dialogRef = me.matDialog.open(me.editorClass, {
      data: me.getEditorData(rec),
      disableClose: true,
      position: { top: '50px' },
    });
    dialogRef.afterClosed().subscribe((data) => {
      me.afterUpdateEditorClosed(data);
    });
  }

  deleteItemHandler(model: any) {
    const me = this;
    me.dialogService
      .confirmDelete(
        me.getDeleteItemConfirmDialogTitle(model),
        me.getDeleteItemConfirmDialogContent(model),
        me.deleteItemConfirmButtonText,
      )
      .subscribe((ok) => {
        if (ok) {
          me.deleteResource(model);
        }
      });
  }

  deleteResource(model: T) {
    const me = this;
    me.http.delete(me.getDeleteResourceUrl(model), me.sendModelOnDelete ? { body: model } : undefined).subscribe(() => {
      me.notifications.snackSuccess(me.getSuccessfullyDeletedItemNotificationContent(model));
      me.removeRecord(model);
    });
  }

  getEditorData(rec?: T): { [key: string]: any, model?: T } {
    return {
      model: rec,
    };
  }

  updateRecord(rec: T) {
    const me = this;
    me.data = me.data?.map((item: any) =>
      item[me.modelIdProperty] != (rec as any)[me.modelIdProperty] ? item : rec
    );
    me.updateView();
  }

  removeRecord(model: T) {
    const me = this;
    me.data = me.data?.filter((rec: T) => {
      return rec != model;
    });
    me.totalRecords--;
    me.updateView();
  }


  /**
   * Este metodo puede ser sobre escrito para evitar el comportamiento por defecto.
   */
  afterCreateEditorClosed(model: T) {
    const me = this;
    if (model) {
      //funcion creada para agregar datos a la matriz
      me.data?.push(model);
      me.updateView();
    }
  }

  /**
   * Este metodo puede ser sobre escrito para evitar el comportamiento por defecto.
   */
  afterUpdateEditorClosed(model: any) {
    const me = this;
    if (model) {
      // Update record
      me.updateRecord(model);
    }
  }

  /**
   * Nota: Sobrescribir este metodo para titulos personalizados.
   */
  getDeleteItemConfirmDialogTitle(model: any): string {
    const me = this;
    if (me.confirmDeleteTitle) return me.confirmDeleteTitle;
    return StringUtils.compileTpl(
      me.confirmDeleteTitleTpl,
      // Capitalize string
      { itemName: me.itemSingularName.charAt(0).toUpperCase() + me.itemSingularName.slice(1) }
    );
  }

  /**
   * Nota: Sobrescribir este metodo para mensajes personalizados
   */
  getDeleteItemConfirmDialogContent(model: T): string {
    return StringUtils.compileTpl(
      this.deleteItemConfirmDialogContentTpl,
      this.getDeleteItemConfirmDialogTplData(model)
    );
  }

  getDeleteItemConfirmDialogTplData(model: T): any {
    const me = this;
    let question = me.deleteItemQuestionText;
    if (!question) {
      question = StringUtils.compileTpl(
        me.deleteItemQuestionTextTpl,
        {
          itemName: me.itemSingularName,
          itemArticle: me.itemSingularArticle
        }
      );
    }
    return {
      question,
      itemName: this.getItemIdentificationName(model),
    };
  }

  /**
   * Nota: Sobrescribir este metodo para mensajes personalizados
   */
  getSuccessfullyDeletedItemNotificationContent(model: T): string {
    return StringUtils.compileTpl(
      this.successfullyDeletedItemNotificationTpl,
      this.getSuccessfullyDeletedItemNotificationTplData(model)
    );
  }

  getSuccessfullyDeletedItemNotificationTplData(model: T): any {
    return {
      itemName: this.getItemIdentificationName(model),
    };
  }

  getItemIdentificationName(model: T): string {
    return (model as any)[this.modelNameProperty] || '';
  }

  reloadHandler() {
    this.loadData();
  }

  ngOnInit(): void {
    const me = this;
    me._subscribeLoadEvent();
    me.subscribeRouteParams();
    me.loadDatasets();
    me._createSelectionModel();
    if (me.autoload) {
      me.loadData();
    }
  }
  subscribeRouteParams() { }
  loadDatasets() { }
  _createSelectionModel() {
    const me = this;
    me.selection = new SelectionModel<T>(me.selectionMultiple, []);
    me.selection.changed.subscribe((selection) => {
      me.selectionChange.emit(selection.source.selected);
    });
  }

  ngAfterViewInit() {
    const me = this;
    if (me.sort) {
      me.sort.sortChange.subscribe(() => {
        if (me.paginator) {
          me.paginator.pageIndex = 0;
        }
        me.loadData();
      });
    }
    if (me.paginator) {
      me.paginator.page.subscribe(() => {
        me.loadData();
      });
    }
  }

  /**
   * Solo notificará los errores de servidor componentes tipo component
   */
  handleServerError(xhr: any) {
    const me = this;
    this.errorStatus = xhr.status;
    if (me.type == COMPONENT_TYPE.COMPONENT) {
      me.notifications.handleXhrError(xhr);
    }
  }

  //*** actualizar las celdas */
  updateView() {
    if (this.table) {
      this.table.renderRows();
    }
  }

  applyFilters(filters: QueryFilter[]) {
    const me = this;
    me.filters = filters;
    me.resetPageIndex();
    me.loadData();
  }

  resetPageIndex() {
    const me = this;
    if (me.paginator) me.paginator.pageIndex = 0;
  }

  getFilters(): QueryFilter[] {
    return this.filters;
  }

  applySearchFilter(filter: QueryFilter) {
    const me = this;
    me.searchFilter = filter;
    if (me.paginator) me.paginator.pageIndex = 0;
    me.loadData();
  }

  applyLocalSearchFilter(filter: QueryFilter) {
    const me = this;
    me.localSearchFilter = filter;
    me.filterData(filter.value);
  }
  /**
   * Determina si se debería o no mostrar el mensaje de cargando
   */
  shouldShowProgressView() {
    const me = this;
    return me._isLoadingData && !me.data;
  }

  shouldShowErrorView() {
    const me = this;
    return me._didLoadDataFailed && !me._isLoadingData;
  }

  // FIX
  shouldShowTitleBar() {
    const me = this;
    return !me._didLoadDataFailed && !me._isLoadingData;
  }

  shouldShowContent() {
    const me = this;
    return !me._didLoadDataFailed && !me._isLoadingData;
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.getSelection().length;
    const numRows = this.data?.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected()
      ? this.selection.clear()
      : this.data?.forEach((row: any) => this.selection.select(row));
  }

  ngOnDestroy(): void {
    if (this._loadSubcription) {
      this._loadSubcription.unsubscribe();
    }
  }

  // Updates a specific field and makes the http request if it's necessary
  updateValue(model: any, fieldName: string, newValue: string | number | boolean) {
    const me = this;
    // No requiere actualizar
    if (model[fieldName] == newValue) {
      return;
    }
    const oldValue = model[fieldName];
    model[fieldName] = newValue;
    me.http
      .put(me.getCollectionUrl() + '/' + model[me.modelIdProperty], model)
      .subscribe(
        (_) => {
          me.afterValueUpdated(model, fieldName, newValue, oldValue);
        },
        (xhr) => {
          // Reset on error
          model[fieldName] = oldValue;
          me.notifications.handleXhrError(xhr);
        }
      );
  }

  afterValueUpdated(model: T, fieldName: string, newValue: any, oldValue: any) {

  }

  navigateToItemView(id: number) {
    this.router.navigate(['./', id], { relativeTo: this.route });
  }

  getBaseHref(): string {
    return document.baseURI;
  }

  getPageIndex() {
    return this.paginator ? this.paginator.pageIndex : this.pageIndex;
  }

  getPageSize() {
    return this.paginator ? this.paginator.pageSize : this.pageSize;
  }

  getItemNumber(index: number): number {
    return this.getPageIndex() * this.getPageSize() + index + 1;
  }

  /**
   * Filtra por todas las columnas
   * @param text Texto buscado
   */
  filterData(text: string) {
    const me = this;
    text = (text || '').toString().trimStart().toLowerCase();
    me.data = me._rawTransformedData.filter((item: any) => {
      return Object.keys(item).some(key => {
        const value = (item[key] || '').toString().toLowerCase();
        return value.includes(text.toLowerCase());
      })
    });
  }

  getErrorStatus(): number {
    return this.errorStatus;
  }

}

